    def longestValidParentheses(self, s: str) -> int:
        if not s: return 0
        if '(' not in s or ')' not in s:
            return 0
        @lru_cache
        def is_valid(ss, reverse_mode=False):
            cnt, ans = 0, 0
            stack = 0
            compare = ')' if reverse_mode else '('
            for c in ss:
                cnt += 1
                if c == compare:
                    stack += 1
                else:
                    stack -= 1
                if stack == 0:
                    ans = max(ans, cnt)
                elif stack < 0:
                    return ans
            return ans
        
